clear all;close all;clc;N=input('Enter the value of N: ');n=0:N-1;k=n;h=zeros(1,length(n));h(1,1)=1;h(1,2)=0.5;HN=fft(h,N);Omega_K=(2*pi*k)./N;stem(Omega_K,10.*log10((abs(HN)).^2),'r','LineWidth',2);hold on;Omega=Omega_K(1):0.01:Omega_K(N);HD=(1.25.*ones(1,length(Omega))+cos(Omega));plot(Omega,10.*log10(HD),'b','LineWidth',2);title(['N point DFT and DTFT of h[n]=\delta[n]+0.5\delta[n-1], where N =',num2str(N)]);xlabel('\Omega_k');ylabel('dB');legend('10log_{10}[|H_{N}[k]|^{2}]','10log_{10}[|H(e^{j\Omega})|^{2}]');