clear;n=0:1:1023;M=64;  %%% Enter an even lengthm=0:1:M-1;h_LPF=0.5.*sinc((m-(M/2))./2);  %%% LPF with BW: v=1/4signal=cos((pi/12).*n)-0.3.*cos((pi/4).*n);root_Ps=norm(signal)/sqrt(1024);SNR=4;figuresubplot(2,2,1)plot(signal) %naxis([100 200 -2 2])title('Signal (s)')white_noise=random('Normal',0,root_Ps/sqrt(SNR),1,length(n));r=signal+white_noise;subplot(2,2,2)plot(r) %ntitle('Signal with "white" noise')axis([100 200 -2 2])y=conv(h_LPF,r);%m=0:1:length(y)-1;subplot(2,2,3)plot(y)title('Low-Pass Filtered Noisy Signal')axis([100+M/2 200+M/2 -2 2])subplot(2,2,4)plot(h_LPF)title('LPF Impulse Response')%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%figureg_HPF=-1.0*h_LPF;g_HPF(M/2+1)=g_HPF(M/2+1)+1;high_freq_noise=conv(g_HPF,white_noise);HF_noise=high_freq_noise(1:length(signal));r_high=signal+HF_noise;z=conv(r_high,h_LPF);subplot(2,2,1)plot(signal) %naxis([100 200 -2 2])title('Signal (s)')subplot(2,2,2)plot(r_high)title('Signal with "colored" noise')axis([100 200 -2 2])subplot(2,2,3)plot(z)title('Signal with "colored" noise -- low-pass filtered')axis([100+M/2 200+M/2 -2 2])subplot(2,2,4)plot(g_HPF)title('HPF')figuresubplot(2,1,1)stem(h_LPF)%axis([M/2-10 M/2+10 -0.8 0.2])title('Low Pass Filter')subplot(2,1,2)stem(g_HPF)%axis([M/2-10 M/2+10 -0.8 0.2])title('High Pass Filter')